mapper.select:
select max(a.trd_dt) max, min(a.trd_dt) min
from(
select a.trd_dt, @rownum:=@rownum+1 num
from fnlab_calendar a, (select @rownum:=0) b
where a.open_gb_stock = '0'
and trd_dt <= #{dt}
order by a.trd_dt desc
) a
where a.num <= #{cnt}

select trd_dt, strt_prc, high_prc, low_prc, cls_prc, yield
		from fnlab_jd_adj a
		where a.trd_dt between #{t0} and #{t1}
		and a.gicode = #{gicode}
		order by trd_dt asc
